mapper.update:
UPDATE relation_customer_asin SET
rca_crawl = #{crawl},
rca_on_sell = #{onSell},
rca_priority = #{priority},
rca_frequency = #{frequency},
rca_star=#{star},
rca_sync_time = #{syncTime}
where
bec_code = #{customerCode}
and
bas_code = #{siteCode}
and
saa_asin = #{asin}

mapper.select:
select * from relation_customer_asin
WHERE
bec_code = #{customerCode}
and
bas_code = #{siteCode}
and
saa_asin = #{asin};
